export namespace Types {
	export const currencies = ['UAH', 'USD', 'EUR']

	/*
        REDUCERS
    */
	export const CURRENCY = 'currency'

	/*
        ACTIONS
    */
	export const GET_CURRENCY = CURRENCY + '/getCurrency'

	/*
        API
    */
	export const API = 'https://api.apilayer.com/fixer/latest'
	export const API_KEY = '88K3HJu5w0I57jEYkXmajMuEgxEUcG3o'
	export const getCurApi = (): string[] => {
		const apiArr: string[] = []
		currencies.forEach((base) => {
			const symbols = currencies.filter((el) => el !== base).join(',')
			apiArr.push(
				`https://api.apilayer.com/fixer/latest?symbols=${symbols}&base=${base}`
			)
		})
		return apiArr
	}

	export const getHeaders = (): Headers => {
		const myHeaders = new Headers()
		myHeaders.append('apikey', API_KEY)
		return myHeaders
	}
}
