export namespace Types {
	export const API_KEY = '88K3HJu5w0I57jEYkXmajMuEgxEUcG3o'
	export const currencies = ['UAH', 'USD', 'EUR']

	/*
        REDUCERS
    */
	export const CURRENCY = 'currency'

	/*
        ACTIONS
    */
	export const GET_CURRENCY = CURRENCY + '/getCurrency'

	/*
        API
    */
	// export const CUR_API = `https://api.apilayer.com/fixer/latest?symbols=${symbols}&base=${base}`
	export const getCurApi = () => {
		const apiArr: string[] = []
		currencies.forEach((base) => {
			const symbols = currencies.filter((el) => el !== base).join(',')
			apiArr.push()
		})
	}
}
