import { OverlayTrigger, Tooltip } from 'react-bootstrap'
import { useAppSelector } from '../../app/hooks'
import { Types } from '../../utils/Types'
import Loader from '../Loader'

interface CurrencyValueProps {
	currency: string
	mainCurrencies: Types.Currency['data'][]
}

const renderTooltip = () => <Tooltip id='cur-tooltip'>Simple tooltip</Tooltip>

export default function CurrencyValue({
	currency,
	mainCurrencies,
}: CurrencyValueProps) {
	const isLoading = useAppSelector((state) => state.currencies.loading)
	const sellObj = mainCurrencies.find((cur) =>
		Object.keys(cur).includes(currency)
	)
	const sell = sellObj ? sellObj[currency].value.toFixed(2) : ''

	const buyObj = mainCurrencies.find((cur) =>
		Object.keys(cur).includes(Types.baseCurrency)
	)
	const buy = buyObj ? buyObj[Types.baseCurrency].value.toFixed(2) : ''
	return isLoading ? (
		<Loader isSmall />
	) : (
		<OverlayTrigger
			placement='right'
			delay={{ show: 250, hide: 400 }}
			overlay={renderTooltip}
		>
			<>{sell + ' | ' + buy}</>
		</OverlayTrigger>
	)
}
