import { createSlice, PayloadAction } from '@reduxjs/toolkit'
import { Types } from '../../utils/Types'
import { Fetch } from '../../utils/Tools'

const initialState: Types.CurrencyState = {
	currency: [],
	loading: false,
}

export const currency = createSlice({
	name: Types.CURRENCY,
	initialState,
	reducers: {},
	extraReducers: (builder) => {
		builder
			.addCase(Fetch.getCurrency.pending, (state) => {
				state.loading = true
			})
			.addCase(
				Fetch.getCurrency.fulfilled,
				(state, action: PayloadAction<Types.Currency>) => {
					state.currency.push(action.payload)
					state.loading = false
				}
			)
	},
})

// export const {} = str.actions
export default currency.reducer
