import React from 'react'
import {
	Dropdown,
	DropdownButton,
	FormControl,
	InputGroup,
} from 'react-bootstrap'
import { useAppDispatch, useAppSelector } from '../../app/hooks'
import { addIn } from '../../app/reducers/currencies'
import { Types } from '../../utils/Types'

export default function Converter() {
	const dispatch = useAppDispatch()
	const inCur = useAppSelector((state) => state.currencies.in)
	const outCur = useAppSelector((state) => state.currencies.out)
	const onClickIn = (e: React.MouseEvent) => {
		const target = e.target as HTMLDivElement
		dispatch(addIn(target.textContent as string))
	}
	return (
		<div className='border border-3 rounded border-dark p-5 mt-5'>
			<>
				<InputGroup className='mb-3'>
					<DropdownButton
						variant='outline-dark'
						title='Currency'
						id='input-group-dropdown-1'
						size='sm'
					>
						{Types.currencies.map((currency) => (
							<Dropdown.Item
								href='#'
								key={currency}
								onClick={onClickIn}
							>
								{currency}
							</Dropdown.Item>
						))}
					</DropdownButton>
					<InputGroup.Text>$</InputGroup.Text>
					<FormControl aria-label='Text input with dropdown button' />
				</InputGroup>
			</>
		</div>
	)
}
