import { createAsyncThunk } from '@reduxjs/toolkit'
import { Types } from './Types'

export namespace Fetch {
	/*
        GET CURRENCY
    */
	export const getCurrency = createAsyncThunk(
		Types.GET_CURRENCY,
		async () => {
			const response = await fetch(Types.CUR_API)
			return response.json()
		}
	)
}
