import { createSlice, PayloadAction } from '@reduxjs/toolkit'
import { Types } from '../../utils/Types'
import { Fetch, Tools } from '../../utils/Tools'

const initialState: Types.CurrencyState = {
	currencies: [],
	loading: false,
	in: 'UAH',
	out: 'USD',
	inValue: '1',
	outValue: '',
	coefficient: 0,
}

export const currencies = createSlice({
	name: Types.CURRENCY,
	initialState,
	reducers: {
		addIn: (state, action: PayloadAction<Types.AvailableCurrencies>) => {
			state.in = action.payload
		},
		addOut: (state, action: PayloadAction<Types.AvailableCurrencies>) => {
			state.out = action.payload
		},
		changeIn: (state, action: PayloadAction<string>) => {
			state.inValue = action.payload
		},
		changeOut: (state, action: PayloadAction<string>) => {
			state.outValue = action.payload
		},
		setCoefficient: (state) => {
			Tools.setCoefficient(state.currencies, state.in, state.out)
		},
	},
	extraReducers: (builder) => {
		builder
			.addCase(Fetch.getCurrency.pending, (state) => {
				state.loading = true
			})
			.addCase(
				Fetch.getCurrency.fulfilled,
				(state, action: PayloadAction<Types.Currency>) => {
					state.currencies.push(action.payload)
					if (state.currencies.length === Types.currencies.length) {
						state.loading = false
						state.coefficient = Tools.setCoefficient(
							state.currencies,
							state.in,
							state.out
						)
					}
				}
			)
	},
})

export const { addIn, addOut, changeIn, changeOut } = currencies.actions
export default currencies.reducer
